import findImports from"./findImports.mjs";import strAfter from"../utilities/strAfter";import makeFilepath from"./makeFilepath.mjs";import isCommonModule from"./isCommonModule.mjs";import makeModuleInfo from"./makeModuleInfo.mjs";export function resolveImports(o){const e=makeFilepath(strAfter(o.base,o.cwd));return findImports(o.contents.toString()).reduce(((o,r)=>{const t=makeModuleInfo(e,r);return t.every(isCommonModule)||t.forEach((e=>o.push(e))),o}),[])}export default resolveImports;